#!/usr/bin/env node

import { execSync } from 'child_process';
import { existsSync, writeFileSync } from 'fs';
import { cpSync } from 'fs';

console.log('üî® Production build with deployment fixes...');

const deploymentTimestamp = new Date().toISOString();

try {
  // Build frontend and backend
  console.log('üèóÔ∏è Building frontend...');
  execSync('vite build', { stdio: 'inherit' });

  console.log('üèóÔ∏è Building backend...');
  execSync('esbuild server/index.ts --platform=node --packages=external --bundle --format=esm --outdir=dist', { stdio: 'inherit' });

  // Copy static files to server directory
  if (existsSync('dist/public')) {
    cpSync('dist/public', 'server/public', { recursive: true });
    console.log('‚úÖ Static files copied to server/public');

    // Create deployment timestamp file for cache busting verification
    writeFileSync('server/public/deploy-timestamp.txt', deploymentTimestamp);
    console.log('‚úÖ Deployment timestamp created');
  } else {
    throw new Error('Frontend build failed - dist/public directory not found');
  }

  console.log('‚úÖ Production build complete!');
  console.log(`üìÖ Deployment timestamp: ${deploymentTimestamp}`);
} catch (error) {
  console.error('‚ùå Build failed:', error.message);
  process.exit(1);
}

server-env-setup.js.content:
#!/usr/bin/env node

/**
 * Server Environment Setup
 * This file patches environment and path issues before loading the main application
 */

import { fileURLToPath } from 'url';
import { dirname, resolve } from 'path';
import { existsSync } from 'fs';

// Setup __dirname equivalent for ES modules globally
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Patch import.meta for bundled code that may have issues
const originalImportMeta = import.meta;

// Set up proper paths for static file serving
const setupPaths = () => {
  const possiblePublicPaths = [
    resolve(__dirname, 'public'),
    resolve(__dirname, 'dist', 'public'),
    resolve(process.cwd(), 'dist', 'public'),
    resolve(process.cwd(), 'public')
  ];

  let publicPath = null;
  for (const path of possiblePublicPaths) {
    if (existsSync(path)) {
      publicPath = path;
      break;
    }
  }

  if (publicPath) {
    process.env.STATIC_FILES_PATH = publicPath;
    console.log(`üìÅ Static files path set to: ${publicPath}`);
  } else {
    console.warn('‚ö†Ô∏è No static files directory found');
    console.warn('   Searched paths:', possiblePublicPaths);
  }

  // Set build path for reference
  process.env.BUILD_DIR = __dirname;
  console.log(`üìÅ Build directory: ${__dirname}`);
};

// Setup error handling for unhandled rejections
process.on('unhandledRejection', (reason, promise) => {
  console.error('üö® Unhandled Rejection at:', promise, 'reason:', reason);
  // Don't exit the process, just log the error
});

// Setup paths
setupPaths();

// Load the main application
console.log('üîÑ Loading main application...');
import('./index.js').catch(error => {
  console.error('‚ùå Failed to load main application:', error);
  process.exit(1);
});

