@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom scrollbar styles for navigation menu */
.nav-scrollbar {
  scrollbar-width: thin;
  scrollbar-color: hsl(var(--border)) transparent;
}

.nav-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.nav-scrollbar::-webkit-scrollbar-track {
  background: transparent;
}

.nav-scrollbar::-webkit-scrollbar-thumb {
  background: hsl(var(--border));
  border-radius: 3px;
}

.nav-scrollbar::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--border) / 0.8);
}

:root {
  /* Light Theme - Inspired by minimalist aesthetic */
  --background: hsl(0, 0%, 99%);
  --foreground: hsl(0, 0%, 8%);
  --card: hsl(0, 0%, 97%);
  --card-foreground: hsl(0, 0%, 8%);
  --popover: hsl(0, 0%, 97%);
  --popover-foreground: hsl(0, 0%, 8%);
  --primary: hsl(0, 0%, 20%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(0, 0%, 95%);
  --secondary-foreground: hsl(0, 0%, 8%);
  --muted: hsl(0, 0%, 95%);
  --muted-foreground: hsl(0, 0%, 55%);
  --accent: hsl(0, 0%, 95%);
  --accent-foreground: hsl(0, 0%, 8%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --border: hsl(0, 0%, 88%);
  --input: hsl(0, 0%, 88%);
  --ring: hsl(0, 0%, 20%);
  --chart-1: hsl(210, 100%, 50%);
  --chart-2: hsl(142, 76%, 36%);
  --chart-3: hsl(280, 65%, 60%);
  --chart-4: hsl(25, 95%, 53%);
  --chart-5: hsl(340, 75%, 55%);
  --radius: 0.75rem;

  /* Minimal Glass Effect - Light */
  --glass-bg: rgba(255, 255, 255, 0.75);
  --glass-border: rgba(0, 0, 0, 0.08);
  --glass-shadow: 0 8px 32px rgba(0, 0, 0, 0.05);

  /* Backgrounds */
  --bg-primary: hsl(0, 0%, 99%);
  --bg-secondary: hsl(0, 0%, 97%);
  --bg-tertiary: hsl(0, 0%, 95%);

  /* Text Colors */
  --text-primary: hsl(0, 0%, 8%);
  --text-secondary: hsl(0, 0%, 35%);
  --text-muted: hsl(0, 0%, 55%);

  /* Borders */
  --border-subtle: hsl(0, 0%, 88%);
  --border-prominent: hsl(0, 0%, 82%);
}

.dark {
  /* Dark Theme - Deep black inspired by Vercel aesthetic */
  --background: hsl(0, 0%, 2%);
  --foreground: hsl(0, 0%, 98%);
  --card: hsl(0, 0%, 4%);
  --card-foreground: hsl(0, 0%, 98%);
  --popover: hsl(0, 0%, 4%);
  --popover-foreground: hsl(0, 0%, 98%);
  --primary: hsl(0, 0%, 90%);
  --primary-foreground: hsl(0, 0%, 10%);
  --secondary: hsl(0, 0%, 8%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --muted: hsl(0, 0%, 8%);
  --muted-foreground: hsl(0, 0%, 45%);
  --accent: hsl(0, 0%, 8%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --border: hsl(0, 0%, 12%);
  --input: hsl(0, 0%, 12%);
  --ring: hsl(0, 0%, 90%);
  --chart-1: hsl(210, 100%, 60%);
  --chart-2: hsl(142, 76%, 36%);
  --chart-3: hsl(280, 65%, 60%);
  --chart-4: hsl(25, 95%, 53%);
