  /* Borders */
  --border-subtle: hsl(0, 0%, 12%);
  --border-prominent: hsl(0, 0%, 18%);
}

/* Clean Background Utilities */
@layer utilities {
  /* Gradient backgrounds */
  .bg-gradient-minimal {
    background: linear-gradient(135deg, 
      hsl(var(--background)) 0%, 
      hsl(var(--muted) / 0.3) 50%, 
      hsl(var(--background)) 100%);
  }

  .bg-gradient-subtle {
    background: linear-gradient(180deg, 
      hsl(var(--background)) 0%, 
      hsl(var(--muted) / 0.2) 100%);
  }

  /* Vercel-style hero gradient */
  .bg-vercel-hero {
    background: linear-gradient(180deg, 
      hsl(var(--background)) 0%, 
      hsl(var(--background)) 70%, 
      hsl(var(--muted) / 0.15) 100%);
  }

  /* Dot pattern background */
  .bg-dots-minimal {
    background-image: radial-gradient(circle, hsl(var(--muted-foreground) / 0.1) 1px, transparent 1px);
    background-size: 24px 24px;
  }

  /* Grid pattern background */
  .bg-grid-minimal {
    background-image: 
      linear-gradient(hsl(var(--border) / 0.3) 1px, transparent 1px),
      linear-gradient(90deg, hsl(var(--border) / 0.3) 1px, transparent 1px);
    background-size: 48px 48px;
  }

  /* Noise texture for depth */
  .bg-noise {
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='1' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.02'/%3E%3C/svg%3E");
  }
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
    scroll-padding-top: 80px; /* Account for sticky header */
  }

  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    font-feature-settings: 'cv02', 'cv03', 'cv04', 'cv11';
    font-optical-sizing: auto;
    font-weight: 400;
    line-height: 1.6;

    /* Mobile-specific improvements */
    -webkit-tap-highlight-color: transparent;
    -webkit-touch-callout: none;
    touch-action: manipulation;

    /* Enhanced smooth scrolling for webkit browsers */
    -webkit-overflow-scrolling: touch;
  }

  /* Enhanced Typography */
  h1, h2, h3, h4, h5, h6 {
    font-weight: 500;
    line-height: 1.2;
    letter-spacing: -0.015em;
  }

  /* Landing page hero animations */
  @keyframes fade-in-up {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-fade-in {
    animation: fade-in-up 1s ease-out forwards;
  }

  .animate-fade-in-delayed {
    animation: fade-in-up 1s ease-out forwards;
    opacity: 0;
  }
}

@layer components {
  /* Minimal Glass Card - Inspired by Vercel aesthetic */
  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(12px);
    border: 1px solid var(--glass-border);
    box-shadow: var(--glass-shadow);
    @apply rounded-xl;
  }

  /* Ultra-minimal background */
  .minimal-bg {
    background: var(--bg-primary);
  }

  /* Elevated surface */
  .elevated-surface {
    background: var(--bg-secondary);
    border: 1px solid var(--border-subtle);
  }

  /* Premium card surface */
  .premium-surface {
    background: var(--bg-tertiary);
    border: 1px solid var(--border-prominent);
  }

  /* Subtle hover effect */
  .hover-lift {
    @apply transition-all duration-200 ease-out;
  }

  .hover-lift:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.12);
  }

  .dark .hover-lift:hover {
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
  }

  /* Minimal button styles */
  .btn-minimal {
    @apply px-6 py-3 rounded-lg font-medium transition-all duration-200;
    background: transparent;
    border: 1px solid var(--border-subtle);
    color: var(--text-secondary);
  }

  .btn-minimal:hover {
    background: var(--bg-secondary);
    color: var(--text-primary);
    transform: translateY(-1px);
  }

  /* Premium navigation */
  .nav-minimal {
    @apply backdrop-blur-md;
    background: rgba(var(--bg-primary-rgb), 0.8);
    border-bottom: 1px solid var(--border-subtle);
  }

  /* Geometric logo shape */
  .logo-triangle {
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-bottom: 17px solid var(--primary);
  }

  /* Enhanced transitions */
  .smooth-transition {
    @apply transition-all duration-300 ease-out;
  }

  .micro-transition {
    @apply transition-all duration-200 ease-out;
  }

  /* Ultra-clean scrollbar */
  .custom-scrollbar {
    scrollbar-width: thin;
    scrollbar-color: var(--border-subtle) transparent;
  }

  .custom-scrollbar::-webkit-scrollbar {
    width: 4px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: transparent;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: var(--border-subtle);
    border-radius: 10px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: var(--border-prominent);
  }

  /* Minimal focus states */
  .focus-minimal:focus {
    outline: none;
    box-shadow: 0 0 0 2px var(--primary);
  }

  /* Typography enhancements */
  .text-gradient {
    background: linear-gradient(135deg, var(--text-primary) 0%, var(--text-secondary) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

@layer utilities {
  .animate-gradient {
    background-size: 200% 200%;
    animation: gradient 15s ease infinite;
  }

  @keyframes gradient {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  /* Mobile-specific touch improvements */
  .mobile-touch {
    min-height: 44px; /* Apple's recommended minimum touch target */
    min-width: 44px;
  }

  .mobile-scroll {
    -webkit-overflow-scrolling: touch;
  }

  /* Better mobile interactions */
  .mobile-friendly {
    -webkit-tap-highlight-color: transparent;
    touch-action: manipulation;
  }

  /* Responsive text sizing */
  @media (max-width: 640px) {
    .text-responsive {
      font-size: 0.9rem;
    }

    .heading-responsive {
      font-size: 1.5rem;
    }
  }
}

