// Deepseek API Service for commodity price predictions
// Note: Deepseek uses OpenAI-compatible API format

class DeepseekService {
  private apiKey: string | undefined;
  private baseURL = 'https://api.deepseek.com/v1';

  constructor() {
    this.apiKey = process.env.DEEPSEEK_API_KEY;
  }

  isConfigured(): boolean {
    return !!this.apiKey;
  }

  async generatePrediction(commodityData: {
    name: string;
    symbol: string;
    currentPrice: number;
    historicalPrices: Array<{ date: string; price: number }>;
    category: string;
    unit: string;
  }): Promise<{
    predictedPrice: number;
    confidence: number;
    reasoning: string;
  }> {
    const prompt = `You are an expert commodity trader specializing in ${commodityData.category} commodities. Analyze ${commodityData.name} (${commodityData.symbol}).

Market Information:
- Current Price: $${commodityData.currentPrice} per ${commodityData.unit}
- Commodity Type: ${commodityData.category}
- Price History (last 7 days): ${this.formatHistoricalData(commodityData.historicalPrices)}

Provide a technical analysis-based price prediction for 7 days ahead. Consider:
- Price momentum and trends
- Market volatility
- Supply chain factors
- Geopolitical influences
- Seasonal patterns

Return your analysis in JSON format:
{
  "predictedPrice": <number>,
  "confidence": <number between 0 and 1>,
  "reasoning": "<concise explanation of prediction methodology>"
}`;

    if (!this.apiKey) {
      throw new Error('Deepseek not configured - missing API key');
    }

    try {
      const response = await fetch(`${this.baseURL}/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: 'deepseek-chat',
          messages: [
            {
              role: 'user',
              content: prompt
            }
          ],
          max_tokens: 1000,
          temperature: 0.7,
          response_format: { type: 'json_object' }
        }),
      });

      if (!response.ok) {
        throw new Error(`Deepseek API error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      const result = JSON.parse(data.choices[0].message.content);

      return {
        predictedPrice: Number(result.predictedPrice),
        confidence: Number(result.confidence),
        reasoning: result.reasoning
      };
    } catch (error) {
      console.error('Deepseek prediction error:', error);
      throw error;
    }
  }

  async generatePredictionWithTimeframe(commodityData: {
    name: string;
    symbol: string;
    currentPrice: number;
    historicalPrices: Array<{ date: string; price: number }>;
    category: string;
    unit: string;
  }, monthsAhead: number): Promise<{
    predictedPrice: number;
    confidence: number;
    reasoning: string;
  }> {
    const prompt = `You are an expert commodity trader specializing in ${commodityData.category} commodities. Analyze ${commodityData.name} (${commodityData.symbol}).

Market Information:
- Current Price: $${commodityData.currentPrice} per ${commodityData.unit}
- Commodity Type: ${commodityData.category}
- Price History (last 7 days): ${this.formatHistoricalData(commodityData.historicalPrices)}

Provide a technical analysis-based price prediction for ${monthsAhead} months ahead. Consider:
- Price momentum and long-term trends
- Market volatility and cyclical patterns
- Supply chain factors and structural changes
- Geopolitical influences
- Seasonal patterns over ${monthsAhead}-month horizon
- Economic cycles and their commodity impact
${monthsAhead <= 3 ? '- Near-term supply disruptions and inventory levels' : ''}
${monthsAhead <= 6 ? '- Seasonal demand patterns and weather impacts' : ''}
${monthsAhead >= 6 ? '- Economic growth trends and industrial demand' : ''}
${monthsAhead >= 9 ? '- Policy changes and regulatory impacts' : ''}
${monthsAhead >= 12 ? '- Long-term structural shifts in supply and demand' : ''}

Return your analysis in JSON format:
{
  "predictedPrice": <number>,
  "confidence": <number between 0 and 1>,
  "reasoning": "<concise explanation of ${monthsAhead}-month prediction methodology>"
}`;

    if (!this.apiKey) {
      throw new Error('Deepseek not configured - missing API key');
    }

    try {
      const response = await fetch(`${this.baseURL}/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: 'deepseek-chat',
          messages: [
            {
              role: 'user',
              content: prompt
            }
          ],
          max_tokens: 1500,
          temperature: 0.7,
          response_format: { type: 'json_object' }
        }),
      });

      if (!response.ok) {
        throw new Error(`Deepseek API error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      const result = JSON.parse(data.choices[0].message.content);

      return {
        predictedPrice: Number(result.predictedPrice),
        confidence: Number(result.confidence),
        reasoning: result.reasoning
      };
    } catch (error) {
      console.error(`Deepseek ${monthsAhead}-month prediction error:`, error);
      throw error;
    }
  }

  private formatHistoricalData(prices: Array<{ date: string; price: number }>): string {
    const recent = prices.slice(-7); // Last 7 days
    return recent.map(p => `${p.date}: $${p.price.toFixed(2)}`).join(', ');
  }
}

export const deepseekService = new DeepseekService();

server/services/fearGreedService.ts.content:
interface FearGreedIndex {
  value: number;
  classification: string;
  timestamp: string;
  previousClose: number;
}

export class FearGreedService {

  /**
   * Get Fear & Greed Index from CNN Money API (free)
   * This provides real market sentiment data
   */
  async getFearGreedIndex(): Promise<FearGreedIndex> {
    try {
      // Use a combination of VIX and market indicators to calculate fear/greed
      const vixData = await this.getVIXData();
      const marketSentiment = await this.calculateMarketSentiment(vixData);

      return {
        value: marketSentiment.value,
        classification: marketSentiment.classification,
        timestamp: new Date().toISOString(),
        previousClose: marketSentiment.previousClose
      };
    } catch (error) {
      console.error('Error fetching Fear & Greed Index:', error);
      // Return neutral fallback data
      return this.getFallbackFearGreed();
    }
  }

  private async getVIXData() {
    try {
      // VIX is a good proxy for market fear/greed
      // Using Yahoo Finance for VIX data (^VIX symbol)
      const response = await fetch(
        `https://query1.finance.yahoo.com/v8/finance/chart/%5EVIX?interval=1d&range=5d`
      );

      if (!response.ok) {
        throw new Error(`VIX API error: ${response.status}`);
      }

      const data = await response.json();
      const result = data?.chart?.result?.[0];

      if (result?.meta?.regularMarketPrice) {
        return {
          current: result.meta.regularMarketPrice,
          previousClose: result.meta.previousClose || result.meta.regularMarketPrice
        };
      }

      throw new Error('Invalid VIX data structure');
    } catch (error) {
      console.error('Error fetching VIX data:', error);
      // Return neutral VIX level
      return { current: 20, previousClose: 20 };
    }
  }

  private async calculateMarketSentiment(vixData: { current: number; previousClose: number }) {
    const { current: vix, previousClose } = vixData;

    // VIX interpretation:
    // 0-12: Extreme Greed
    // 12-17: Greed  
    // 17-25: Neutral
    // 25-35: Fear
    // 35+: Extreme Fear

    let value: number;
    let classification: string;

    if (vix <= 12) {
      value = 85; // Extreme Greed
      classification = "Extreme Greed";
    } else if (vix <= 17) {
      value = 70; // Greed
      classification = "Greed";
    } else if (vix <= 25) {
      value = 50; // Neutral
      classification = "Neutral";
    } else if (vix <= 35) {
      value = 25; // Fear
      classification = "Fear";
    } else {
      value = 10; // Extreme Fear
      classification = "Extreme Fear";
    }

    // Add some variation based on recent movement
    const movement = ((vix - previousClose) / previousClose) * 100;
    if (Math.abs(movement) > 5) {
      // Significant movement adjusts sentiment
      value += movement > 0 ? -5 : 5; // Higher VIX = more fear
      value = Math.max(0, Math.min(100, value));
    }

    return {
      value: Math.round(value),
      classification,
      previousClose
    };
  }

  private getFallbackFearGreed(): FearGreedIndex {
    // Generate a realistic fallback based on current date
    const now = new Date();
    const dayOfYear = Math.floor((now.getTime() - new Date(now.getFullYear(), 0, 0).getTime()) / 86400000);

    // Create some variation based on day of year (sine wave between 25-75)
    const baseValue = 50 + (Math.sin(dayOfYear / 365 * Math.PI * 2) * 25);
    const value = Math.round(baseValue);

    let classification: string;
    if (value >= 80) classification = "Extreme Greed";
    else if (value >= 60) classification = "Greed";
    else if (value >= 40) classification = "Neutral";
    else if (value >= 20) classification = "Fear";
    else classification = "Extreme Fear";

    return {
      value,
      classification,
      timestamp: now.toISOString(),
      previousClose: value - 2 // Slight variation
    };
  }
}

