import { DatabaseStorage } from '../storage';

export class StartupManager {
  private storage: DatabaseStorage;

  constructor(storage: DatabaseStorage) {
    this.storage = storage;
  }

  // Critical startup - must complete successfully
  async initializeCritical(): Promise<void> {
    console.log('üîß Initializing critical services...');

    // Test database connection
    await this.storage.ensureConnection();
    console.log('‚úÖ Database connection verified');

    // CRITICAL: Run migrations FIRST before any other database operations
    await this.storage.runAutomaticMigrations();
    console.log('‚úÖ Database migrations completed');

    // Initialize default data (models + commodities)
    await this.storage.ensureDefaultData();
    console.log('‚úÖ Database schema and default data initialized');
  }

  // Heavy operations - can be delayed
  async initializeHeavy(): Promise<void> {
    console.log('‚ö° Starting heavy initialization (background)...');

    try {
      // Get cryptocurrencies from verified database
      const cryptocurrencies = await this.storage.getCryptocurrencies();
      console.log(`üìä Found ${cryptocurrencies.length} cryptocurrencies in database`);

      // Initialize price data in background (don't block startup)
      this.initializePricesInBackground(cryptocurrencies);

    } catch (error) {
      console.error('‚ùå Heavy initialization failed (non-critical):', error);
      // Don't throw - app can work without historical data
    }
  }

  private initializePricesInBackground(cryptocurrencies: any[]): void {
    // Run in background without blocking startup
    setTimeout(async () => {
      try {
        console.log('üîÑ Starting background price data initialization...');

        // Initialize CoinGecko crypto data
        const { coinGeckoService } = await import('./coinGeckoService');

        for (const cryptocurrency of cryptocurrencies) {
          try {
            await coinGeckoService.updateCryptoPrices(cryptocurrency.id);
          } catch (error) {
            console.log(`‚ö†Ô∏è Could not initialize prices for ${cryptocurrency.name}:`, (error as Error).message);
          }
        }

        // Start prediction scheduler
        const { predictionScheduler } = await import('./predictionScheduler');
        predictionScheduler.start();

        // Run initial AI predictions on first deployment
        await this.runInitialPredictions();

        // Check for missing Claude predictions and auto-generate
        await this.checkAndGenerateMissingClaudePredictions();

        console.log('‚úÖ Background initialization complete');
      } catch (error) {
        console.error('‚ùå Background initialization failed:', error);
      }
    }, 5000); // 5 second delay to ensure app is fully started
  }

  // Run initial AI predictions on first deployment
  private async runInitialPredictions(): Promise<void> {
    try {
      console.log('ü§ñ Checking for initial AI predictions...');

      // Check if we already have quarterly predictions specifically
      const allPredictions = await this.storage.getPredictions();
      const quarterlyPredictions = allPredictions.filter(p => 
        p.timeframe && ['3mo', '6mo', '9mo', '12mo'].includes(p.timeframe)
      );

      if (quarterlyPredictions.length === 0) {
        console.log('üöÄ No quarterly predictions found - triggering automatic quarterly prediction generation...');

        // Import AI prediction service
        const { aiPredictionService } = await import('./aiPredictionService');

        // Force quarterly prediction generation regardless of API key configuration
        // This will work in production with AI keys, and gracefully handle missing keys in dev
        console.log('üîÆ Starting automatic quarterly prediction generation for all commodities...');
        console.log('üìÖ This will generate 3mo, 6mo, 9mo, and 12mo predictions for all AI models');

        try {
          await aiPredictionService.generateMonthlyPredictions();
          console.log('‚úÖ Automatic quarterly prediction generation completed successfully');
        } catch (error) {
          console.log('‚ö†Ô∏è Quarterly prediction generation encountered issues (this is expected in dev without AI keys):', (error as Error).message);
          console.log('üí° This will work properly in production with configured AI keys');
        }
      } else {
        console.log(`üìä Found ${quarterlyPredictions.length} existing quarterly predictions - skipping automatic generation`);
      }
    } catch (error) {
      console.error('‚ùå Initial prediction check failed (non-critical):', error);
      // Don't throw - this is non-critical for app startup
    }
  }

  // Check for missing Claude predictions and auto-generate
  private async checkAndGenerateMissingClaudePredictions(): Promise<void> {
    try {
      console.log('üîç Checking for missing Claude predictions...');

      // Import required services
      const { aiPredictionService } = await import('./aiPredictionService');
      const { claudeService } = await import('./claudeService');

      // Only proceed if Claude is configured (production has API key)
      if (!claudeService.isConfigured()) {
        console.log('‚ö†Ô∏è Claude not configured - skipping missing prediction check');
        return;
      }

      // Get all commodities and Claude model
      const commodities = await this.storage.getCommodities();
      const aiModels = await this.storage.getAiModels();
      const claudeModel = aiModels.find(model => model.name.toLowerCase() === 'claude');

      if (!claudeModel) {
        console.log('‚ö†Ô∏è Claude model not found in database - skipping check');
        return;
      }

      // Check each commodity for missing Claude predictions
      const commoditiesNeedingPredictions: string[] = [];

      for (const commodity of commodities) {
        // Get Claude predictions for this commodity from last 7 days
        const recentPredictions = await this.storage.getPredictions(commodity.id, claudeModel.id);
        const cutoffDate = new Date();
        cutoffDate.setDate(cutoffDate.getDate() - 7);

        const recentClaudePredictions = recentPredictions.filter(pred => 
          new Date(pred.createdAt!) > cutoffDate
        );

        if (recentClaudePredictions.length === 0) {
          commoditiesNeedingPredictions.push(commodity.id);
          console.log(`üìù Missing Claude predictions for: ${commodity.name}`);
        }
      }

      // Auto-generate missing predictions
      if (commoditiesNeedingPredictions.length > 0) {
        console.log(`üöÄ Auto-generating Claude predictions for ${commoditiesNeedingPredictions.length} commodities...`);

        console.log('‚ö†Ô∏è Weekly prediction generation has been disabled. Only monthly predictions are available.');
        // Weekly predictions have been removed from the system

        console.log('‚úÖ Auto-generation of missing Claude predictions completed');
      } else {
        console.log('‚úÖ All commodities have recent Claude predictions');
      }

    } catch (error) {
      console.error('‚ùå Missing Claude prediction check failed (non-critical):', error);
      // Don't throw - this is non-critical for app startup
    }
  }
}

server/services/yahooFinance.ts.content:
import { storage } from "../storage";
import type { InsertActualPrice } from "@shared/schema";

interface YahooFinanceResponse {
  chart: {
    result: Array<{
      meta: {
        symbol: string;
        regularMarketPrice: number;
        regularMarketChange?: number;
        regularMarketChangePercent?: number;
        currency: string;
      };
      timestamp: number[];
      indicators: {
        quote: Array<{
          close: number[];
          volume: number[];
        }>;
      };
    }>;
  };
}

class YahooFinanceService {
  private rateLimitDelay = 2000; // 2 seconds between requests to avoid rate limiting
  private lastRequestTime = 0;

  private async delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  private async enforceRateLimit(): Promise<void> {
    const now = Date.now();
    const timeSinceLastRequest = now - this.lastRequestTime;

    if (timeSinceLastRequest < this.rateLimitDelay) {
      await this.delay(this.rateLimitDelay - timeSinceLastRequest);
    }

    this.lastRequestTime = Date.now();
  }

  async fetchHistoricalData(yahooSymbol: string, period = "7d", interval = "1d"): Promise<YahooFinanceResponse | null> {
    await this.enforceRateLimit();

    try {
      const url = `https://query1.finance.yahoo.com/v8/finance/chart/${yahooSymbol}?range=${period}&interval=${interval}`;

      const response = await fetch(url, {
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
          'Accept': 'application/json',
          'Accept-Language': 'en-US,en;q=0.9',
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        }
      });

      if (!response.ok) {
        console.error(`Yahoo Finance API error: ${response.status} ${response.statusText}`);
        return null;
      }

      const data = await response.json() as YahooFinanceResponse;
      return data;
    } catch (error) {
      console.error(`Error fetching data for ${yahooSymbol}:`, error);
      return null;
    }
  }

  async updateCommodityPrices(commodityId?: string): Promise<void> {
    try {
      const commodities = commodityId 
        ? [await storage.getCommodity(commodityId)].filter(Boolean)
        : await storage.getCommodities();

      for (const commodity of commodities) {
        if (!commodity?.yahooSymbol) continue;

        console.log(`Fetching data for ${commodity.name} (${commodity.yahooSymbol})`);

        const data = await this.fetchHistoricalData(commodity.yahooSymbol);

        if (data?.chart?.result?.[0]) {
          const result = data.chart.result[0];
          const timestamps = result.timestamp || [];
          const quotes = result.indicators?.quote?.[0];

          if (quotes?.close) {
            for (let i = 0; i < timestamps.length; i++) {
              const timestamp = timestamps[i];
              const price = quotes.close[i];
              const volume = quotes.volume?.[i];

              if (price && !isNaN(price)) {
                const actualPrice: InsertActualPrice = {
                  commodityId: commodity.id,
                  date: new Date(timestamp * 1000),
                  price: price.toString(),
                  volume: volume ? volume.toString() : null,
                  source: "yahoo_finance"
                };

                await storage.createActualPrice(actualPrice);
              }
            }
          }
        }
      }
    } catch (error) {
      console.error('Error updating commodity prices:', error);
      throw error;
    }
  }

  async getCurrentPrice(yahooSymbol: string): Promise<{ price: number; change?: number; changePercent?: number } | null> {
    // Fetch 5 days of data to ensure we have enough for calculating 24h change
    const data = await this.fetchHistoricalData(yahooSymbol, "5d", "1d");

    if (data?.chart?.result?.[0]) {
      const result = data.chart.result[0];
      const meta = result.meta;
      const currentPrice = meta.regularMarketPrice;

      // Try to get change from Yahoo Finance API first
      let change = meta.regularMarketChange;
      let changePercent = meta.regularMarketChangePercent;

      // If Yahoo doesn't provide change data, calculate it manually
      if ((change === undefined || change === 0) && result.indicators?.quote?.[0]?.close) {
        const prices = result.indicators.quote[0].close;
        const timestamps = result.timestamp || [];

        if (prices.length >= 2 && timestamps.length >= 2) {
          // Get the previous trading day's closing price
          const previousClose = prices[prices.length - 2];

          if (previousClose && !isNaN(previousClose) && currentPrice && !isNaN(currentPrice)) {
            change = currentPrice - previousClose;
            changePercent = (change / previousClose) * 100;
          }
        }
      }

      return {
        price: currentPrice,
        change: change || 0,
        changePercent: changePercent || 0
      };
    }

    return null;
  }

  async fetchDetailedHistoricalData(yahooSymbol: string, period: string): Promise<any[]> {
    await this.enforceRateLimit();

    const intervalMap: Record<string, string> = {
      "1d": "5m",
      "5d": "15m", 
      "1w": "30m",
      "1mo": "1d",
      "3mo": "1d",
      "6mo": "1d",
      "1y": "1d",
      "2y": "1wk",
      "5y": "1mo",
      "10y": "1mo",
      "max": "1mo",
    };

    const interval = intervalMap[period] || "1d";

    // Alternative symbols for commodities with better historical data
    const alternativeSymbols: Record<string, string[]> = {
      "CL=F": ["CL=F", "USO", "DBOIL"], // Crude oil alternatives
      "GC=F": ["GC=F", "GLD", "IAU"],   // Gold alternatives
      "NG=F": ["NG=F", "UNG", "BOIL"],  // Natural gas alternatives
      "HG=F": ["HG=F", "CPER"],        // Copper alternatives
      "SI=F": ["SI=F", "SLV"],         // Silver alternatives
    };

    const symbolsToTry = alternativeSymbols[yahooSymbol] || [yahooSymbol];

    for (const symbol of symbolsToTry) {
      try {
        console.log(`Attempting to fetch ${period} data for ${symbol}`);
        const data = await this.fetchHistoricalData(symbol, period, interval);

        if (data?.chart?.result?.[0]) {
          const result = data.chart.result[0];
          const timestamps = result.timestamp || [];
          const quotes = result.indicators?.quote?.[0];

          if (quotes?.close && timestamps.length > 0) {
            const processedData = timestamps.map((timestamp: number, i: number) => ({
              date: new Date(timestamp * 1000).toISOString(),
              price: quotes.close[i],
              volume: quotes.volume?.[i] || 0,
            })).filter(item => item.price && !isNaN(item.price));

            if (processedData.length > 0) {
              console.log(`Successfully fetched ${processedData.length} data points from ${symbol} for period ${period}`);
              return processedData;
            }
          }
        }

        console.log(`No data available for ${symbol}, trying next alternative...`);

        // Add delay between attempts to avoid rate limiting
        if (symbolsToTry.indexOf(symbol) < symbolsToTry.length - 1) {
          await this.delay(2000);
        }

      } catch (error) {
        console.error(`Error fetching data for ${symbol}:`, error);

        // If it's a rate limit error, wait longer
        if ((error as Error).message?.includes('Too Many Requests') || (error as Error).message?.includes('429')) {
          console.log('Rate limit detected, waiting 5 seconds...');
          await this.delay(5000);
        }

        continue; // Try next symbol
      }
    }

    console.warn(`Failed to fetch data for all alternatives of ${yahooSymbol} for period ${period}`);
    return [];
  }
}

export const yahooFinanceService = new YahooFinanceService();

server/services/yahooFinanceCacheService.content:
import { yahooFinanceService } from "./yahooFinance";
import { storage } from "../storage";

interface CachedPriceData {
  price: number;
  change?: number;
  changePercent?: number;
  timestamp: Date;
  yahooSymbol: string;
}

interface CachedChartData {
  data: any[];
  timestamp: Date;
  yahooSymbol: string;
  period: string;
}

class YahooFinanceCacheService {
  private priceCache = new Map<string, CachedPriceData>();
  private chartCache = new Map<string, CachedChartData>();

  // Cache durations in milliseconds
  private readonly PRICE_CACHE_DURATION = 2 * 60 * 1000; // 2 minutes for real-time prices
  private readonly CHART_CACHE_DURATION = 15 * 60 * 1000; // 15 minutes for chart data
  private readonly BACKGROUND_REFRESH_INTERVAL = 5 * 60 * 1000; // 5 minutes background refresh

  private refreshTimer?: NodeJS.Timeout;

  constructor() {
    this.startBackgroundRefresh();
  }

  /**
   * Get current price with server-side caching
   * Returns cached data if available and fresh, otherwise fetches from Yahoo Finance
   */
  async getCachedCurrentPrice(yahooSymbol: string): Promise<CachedPriceData | null> {
    const cacheKey = `price:${yahooSymbol}`;
    const cached = this.priceCache.get(cacheKey);

    // Return cached data if it's fresh (within cache duration)
    if (cached && this.isCacheDataFresh(cached.timestamp, this.PRICE_CACHE_DURATION)) {
      console.log(`üì¶ Cache HIT for ${yahooSymbol} price (age: ${Math.round((Date.now() - cached.timestamp.getTime()) / 1000)}s)`);
      return cached;
    }

    console.log(`üîÑ Cache MISS for ${yahooSymbol} price - fetching fresh data`);

    try {
      // Fetch fresh data from Yahoo Finance
      const priceData = await yahooFinanceService.getCurrentPrice(yahooSymbol);

      if (priceData) {
        const cachedData: CachedPriceData = {
          price: priceData.price,
          change: priceData.change,
          changePercent: priceData.changePercent,
          timestamp: new Date(),
          yahooSymbol
        };

        // Store in cache
        this.priceCache.set(cacheKey, cachedData);
        console.log(`üíæ Cached price for ${yahooSymbol}: $${priceData.price}`);

        return cachedData;
      }
    } catch (error) {
      console.error(`‚ùå Failed to fetch price for ${yahooSymbol}:`, error);

      // Return stale cache data if available (graceful fallback)
      if (cached) {
        console.log(`‚ö†Ô∏è Returning stale cache data for ${yahooSymbol} due to API failure`);
        return cached;
      }
    }

    return null;
  }

  /**
   * Get chart data with server-side caching
   */
  async getCachedChartData(yahooSymbol: string, period: string): Promise<any[] | null> {
    const cacheKey = `chart:${yahooSymbol}:${period}`;
    const cached = this.chartCache.get(cacheKey);

    // Return cached data if it's fresh
    if (cached && this.isCacheDataFresh(cached.timestamp, this.CHART_CACHE_DURATION)) {
      console.log(`üì¶ Cache HIT for ${yahooSymbol} chart data (${period}) (age: ${Math.round((Date.now() - cached.timestamp.getTime()) / 1000)}s)`);
      return cached.data;
    }

    console.log(`üîÑ Cache MISS for ${yahooSymbol} chart data (${period}) - fetching fresh data`);

    try {
      // Fetch fresh chart data from Yahoo Finance
      const chartData = await yahooFinanceService.fetchDetailedHistoricalData(yahooSymbol, period);

      if (chartData && chartData.length > 0) {
        const cachedData: CachedChartData = {
          data: chartData,
          timestamp: new Date(),
          yahooSymbol,
          period
        };

        // Store in cache
        this.chartCache.set(cacheKey, cachedData);
        console.log(`üíæ Cached chart data for ${yahooSymbol} (${period}): ${chartData.length} data points`);

        return chartData;
      }
    } catch (error) {
      console.error(`‚ùå Failed to fetch chart data for ${yahooSymbol} (${period}):`, error);

      // Return stale cache data if available (graceful fallback)
      if (cached) {
        console.log(`‚ö†Ô∏è Returning stale chart data for ${yahooSymbol} due to API failure`);
        return cached.data;
      }
    }

    return null;
  }

  /**
   * Background refresh of all cached commodity prices
   * This eliminates the need for real-time API calls from user requests
   */
  private async refreshAllCommodityPrices(): Promise<void> {
    try {
      console.log(`üîÑ Starting background price refresh for all commodities...`);
      const cryptocurrencies = await storage.getCryptocurrencies();

      let refreshedCount = 0;
      let errorCount = 0;

      for (const cryptocurrency of cryptocurrencies) {
        if (!cryptocurrency.symbol) continue;

        try {
          // This will fetch fresh data and update the cache - using symbol for now
          await this.getCachedCurrentPrice(cryptocurrency.symbol + "-USD");
          refreshedCount++;

          // Add small delay between requests to respect rate limits
          await new Promise(resolve => setTimeout(resolve, 1000));
        } catch (error) {
          console.error(`Error refreshing price for ${cryptocurrency.name}:`, error);
          errorCount++;
        }
      }

      console.log(`‚úÖ Background refresh complete: ${refreshedCount} prices updated, ${errorCount} errors`);
    } catch (error) {
      console.error('‚ùå Background price refresh failed:', error);
    }
  }

  /**
   * Start background refresh timer
   */
  private startBackgroundRefresh(): void {
    console.log(`üöÄ Starting background price refresh every ${this.BACKGROUND_REFRESH_INTERVAL / 1000} seconds`);

    // Do initial refresh after 30 seconds (let app start up first)
    setTimeout(() => {
      this.refreshAllCommodityPrices();
    }, 30000);

    // Set up recurring refresh
    this.refreshTimer = setInterval(() => {
      this.refreshAllCommodityPrices();
    }, this.BACKGROUND_REFRESH_INTERVAL);
  }

  /**
   * Stop background refresh (for cleanup)
   */
  public stopBackgroundRefresh(): void {
    if (this.refreshTimer) {
      clearInterval(this.refreshTimer);
      this.refreshTimer = undefined;
      console.log('üõë Background price refresh stopped');
    }
  }

  /**
   * Check if cached data is still fresh
   */
  private isCacheDataFresh(timestamp: Date, maxAge: number): boolean {
    return (Date.now() - timestamp.getTime()) < maxAge;
  }

  /**
   * Get cache statistics for monitoring
   */
  public getCacheStats(): { priceCache: number; chartCache: number } {
    return {
      priceCache: this.priceCache.size,
      chartCache: this.chartCache.size
    };
  }

  /**
   * Clear expired cache entries (optional cleanup)
   */
  public cleanupExpiredCache(): void {
    const now = Date.now();

    // Clean price cache
    for (const [key, data] of Array.from(this.priceCache.entries())) {
      if (!this.isCacheDataFresh(data.timestamp, this.PRICE_CACHE_DURATION * 2)) { // Keep for 2x duration
        this.priceCache.delete(key);
      }
    }

    // Clean chart cache
    for (const [key, data] of Array.from(this.chartCache.entries())) {
      if (!this.isCacheDataFresh(data.timestamp, this.CHART_CACHE_DURATION * 2)) { // Keep for 2x duration
        this.chartCache.delete(key);
      }
    }
  }
}

// Export singleton instance
export const yahooFinanceCacheService = new YahooFinanceCacheService();

