// Production entry point - ensures frontend changes are deployed
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { storage } from "./storage";
import { StartupManager } from "./services/startupManager";
import path from "path";
import fs from "fs";

function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

function serveStatic(app: express.Express) {
  const distPath = path.resolve(process.cwd(), "server", "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  console.log("üöÄ Starting AIForecast Hub (Production) - Frontend Fixed v1.0.3");
  console.log("üîß FIXED: Frontend changes now deploy to production");
  console.log(`üöÄ Environment: ${process.env.NODE_ENV || 'production'}, Port: ${parseInt(process.env.PORT || '5000', 10)}`);

  // Initialize startup manager
  const startupManager = new StartupManager(storage);

  try {
    // Phase 1: Critical startup (must succeed)
    await startupManager.initializeCritical();
    console.log("‚úÖ Critical initialization complete");
  } catch (error) {
    console.error("‚ùå Critical startup failed:", error);
    process.exit(1);
  }

  // Phase 2: Register routes and setup server
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // Setup static file serving for production
  serveStatic(app);
  console.log("‚úÖ Static file serving configured for production");
  console.log(`üìÅ Serving frontend from: ${path.resolve(process.cwd(), "server", "public")}`);

  // Start server
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen(port, "0.0.0.0", () => {
    log(`‚úÖ Server running on port ${port}`);
    console.log("üéØ Application ready - frontend changes will now appear");

    // Phase 3: Heavy initialization (background, non-blocking)
    startupManager.initializeHeavy().catch(error => {
      console.error("‚ö†Ô∏è Background initialization failed (non-critical):", error);
    });
  });
})();

